import React from 'react';
import { makeStyles } from "@material-ui/styles";

const styles = makeStyles({
    container: {
        "& p": {
            color: "#ccc"
        },
        "& label": {
            "& input": {
                border: "none",
                outline: "none",
                borderBottom : "1px solid #ccc"
            },
        },
    },
});

export default function SlidingPlaceholderInput({ value, placeholder }){
    const classes = styles();
    return (
        <div className = {classes.container}>
            <p>{placeholder}</p>
            <label><input onChange = {onChange} value = {value}/></label>
        </div>
    )
}
