import React from 'react';
import { makeStyles } from "@material-ui/styles";

const styles = makeStyles({
    container: {
        "& p": {
            color: "#ccc",
            position: "relative",
            top: "50%",
            left: "50%",
            transform: "translateX(-50%)",
            paddingBottom: 10,
            cursor: "text"
        },
        "& label": {
            "& input": {
                border: "none",
                outline: "none",
                borderBottom : "1px solid #ccc",
                zIndex: "100"
            },
        },
    },
});

export default function SlidingPlaceholderInput({ value, placeholder, onChange }){
    const classes = styles();
    function handleFocus(e){
        console.log(e.currentTarget)
    }
    function handleClick(e){
        document.querySelector(".inp").focus();
        e.currentTarget.style.transform = "translateY(-20%)"
    }
    return (
        <div className = {classes.container}>
            <p onClick = {handleClick}>{placeholder}</p>
            <label><input className = "inp" onFocus = {handleFocus} onChange = {onChange} value = {value}/></label>
        </div>
    )
}
